import { createTheme } from '@mui/material/styles';

const DINFont = {
    fontFamily: 'DIN',
    fontStyle: 'normal',
    fontDisplay: 'swap',
    fontWeight: 400,
    src: `
    local('DIN'),
    url('https://db.onlinewebfonts.com/c/5b0be84ee7019608e3e098c541290494?family=DINNextLTPro-Regular') format('truetype')
  `,
};

const theme = createTheme({
    palette: {
        primary: {
            main: '#003971',
        },
        secondary: {
            main: '#f44336',
        },
    },
    typography: {
        fontFamily: 'DIN, Roboto, sans-serif', // add DIN font to font family
    },
    //appbar has max 1024px width and is centered
    //appbar has DIN bold font 
    //text color #6BF3BF
    //appbar has a background color #003971
    //appbar has a height of 64px

    components: {
        MuiAppBar: {
            styleOverrides: {
                root: {
                    maxWidth: '1024px',
                    margin: 'auto',
                    fontFamily: 'DIN, Roboto, sans-serif',
                    color: '#6BF3BF',
                    backgroundColor: '#003971',
                    height: '64px',
                },
            },
        },
        MuiCssBaseline: {
            styleOverrides: {
                '@global': {
                    '@font-face': [DINFont], // add @font-face rule to load DIN font
                },
            },
        },
    },
});

export default theme;
