import React from 'react';
import NavigationButtons from './NavButtons';
import AssessmentContent from './AssessmentContent';
import CheckboxField from './CheckboxField';
import { List, ListSubheader, ListItemButton, ListItemIcon, Divider, Box, Typography } from '@mui/material';
import Grid2 from '@mui/material/Unstable_Grid2/Grid2';


const Step2 = ({ prevStep, nextStep }) => {
    return (
        <React.Fragment>
            <AssessmentContent
                title="Delivering working, tested software every 3 weeks or less"
                prevStep={prevStep}
                nextStep={nextStep}>
                <Grid2 container spacing={2}>
                    <Grid2 item xs={4}>
                        <Box>
                            <Typography variant='h6'>Iterations</Typography>
                            <List sx={{ textAlign: 'left' }}>
                                <CheckboxField name="iterations" label="Timeboxed Iterations" />
                                <List component="div" disablePadding sx={{ pl: 6, pr: 2, mr: 2 }}>
                                    <CheckboxField name="endOnTime" label="Always end on time" />
                                    <CheckboxField name="undisturbed" label="Team not disrupted or controlled by outsiders" />
                                </List>

                            </List>
                        </Box>
                    </Grid2>
                    <Grid2 item xs={4}>
                        <Box sx={{ border: 1, borderColor: 'divider', borderRadius: 1 }}>
                        </Box>
                    </Grid2>
                    <Grid2 item xs={4}>
                        <Box sx={{ border: 1, borderColor: 'divider', borderRadius: 1 }}>
                        </Box>
                    </Grid2>
                </Grid2>
            </AssessmentContent>
            <NavigationButtons prevStep={prevStep} nextStep={nextStep} />
        </React.Fragment >
    );
};

export default Step2;
