import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import Container from '@mui/material/Container';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Box from '@mui/material/Box';
import Divider from '@mui/material/Divider';
import Stack from '@mui/material/Stack';
import Item from '@mui/material/Stack';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import BasicsStep1 from './Basics';
const AssessmentForm = () => {
    const [step, setStep] = useState(1);

    const nextStep = () => {
        setStep(step + 1);
    };
    const prevStep = () => {
        setStep(step - 1);
    };
    const navigate = useNavigate();
    const formik = useFormik({
        initialValues: {
            firstName: '',
            lastName: '',
            email: '',
        },
        validationSchema: Yup.object({
            firstName: Yup.string()
                .max(15, 'Must be 15 characters or less')
                .required('Required'),
            lastName: Yup.string()
                .max(20, 'Must be 20 characters or less')
                .required('Required'),
            email: Yup.string().email('Invalid email address').required('Required'),
        }),
        onSubmit: (values) => {
            navigate('/basics');
        },
        handleSubmit: (values, { setSubmitting }) => {
            setTimeout(() => {
                setSubmitting(false);
            }, 400);
            navigate('/basics');
        }
    });
    return (
        <Container maxWidth="sm">
            <Box sx={{ bgcolor: 'green' }}>
                <Typography variant="body1" gutterBottom>
                    In this assessment, we will ask you a series of questions to determine how well your organization is doing with Agile.
                </Typography>
                <Divider />
            </Box>
            <Box sx={{ bgcolor: '#cfe8fc' }}>
                <Divider />
                <form onSubmit={formik.handleSubmit}>
                    <FormGroup>
                        <Stack spacing={2}>
                            <Item>
                                <FormControlLabel control={<Checkbox />} label="Delivering working, tested software every 3 weeks or less" />
                            </Item>
                            <Item>
                                <FormControlLabel control={<Checkbox />} label="Delivering what the business needs most" />
                            </Item>
                            <Item>
                                <FormControlLabel control={<Checkbox />} label="Process is continuously improving" />
                            </Item>
                        </Stack>
                    </FormGroup>
                    <Button variant="contained" color="primary" type="submit">
                        Submit
                    </Button>
                </form>
            </Box>
        </Container>
    );
};

export default AssessmentForm;
