import { createTheme } from '@mui/material/styles';

const DINFont = {
    fontFamily: 'DIN',
    fontStyle: 'normal',
    fontDisplay: 'swap',
    fontWeight: 400,
    src: `
    local('DIN'),
    url('https://db.onlinewebfonts.com/c/5b0be84ee7019608e3e098c541290494?family=DINNextLTPro-Regular') format('truetype')
  `,
};


const DINFontBold = {
    fontFamily: 'DIN',
    fontStyle: 'bold',
    fontDisplay: 'swap',
    fontWeight: 400,
    src: `
    url('https://db.onlinewebfonts.com/c/3a88649e176a40a6d80b395ca0ae430d?family=DINNextLTPro-Bold') format('truetype')
  `,
};

const theme = createTheme({

    palette: {
        primary: {
            main: '#3f51b5',
        },
        secondary: {
            main: '#f50057',
        },
        background: {
            paper: '#f1ceff',
        },
        text: {
            primary: '#003a75',
            disabled: '#93b1c8',
            secondary: '#ffffff',
        },
    },
    typography: {
        h1: {
            fontFamily: "DINNextLTPro-Bold",
            lineHeight: 1.75,
        },
        h5: {
            fontFamily: DINFont,
        },
        fontFamily: 'DIN, Roboto, sans-serif', // add DIN font to font family
    },

    components: {
        MuiAppBar: {
            styleOverrides: {
                root: {
                    margin: 'auto',
                    fontFamily: 'DIN, Roboto, sans-serif',
                    fontWeight: 'bold',
                    fontSize: '24px',
                    textAlign: 'center',
                    padding: '10px',
                    color: '#6BF3BF',
                    backgroundColor: '#003971',
                    height: '64px',
                },
            },
        },
        MuiCssBaseline: {
            styleOverrides: {
                '@global': {
                    '@font-face': [DINFont], // add @font-face rule to load DIN font
                },
            },
        },
    },
});

export default theme;
