import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import Container from '@mui/material/Container';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Box from '@mui/material/Box';
import Divider from '@mui/material/Divider';
import Stack from '@mui/material/Stack';

const WelcomeForm = () => {
    const navigate = useNavigate();
    const formik = useFormik({
        initialValues: {
            firstName: '',
            lastName: '',
            email: '',
        },
        validationSchema: Yup.object({
            firstName: Yup.string()
                .max(15, 'Must be 15 characters or less')
                .required('Required'),
            lastName: Yup.string()
                .max(20, 'Must be 20 characters or less')
                .required('Required'),
            email: Yup.string().email('Invalid email address').required('Required'),
        }),
        onSubmit: (values) => {
            navigate('/welcome');
        },
    });
    return (
        <Container maxWidth="sm">
            <Box sx={{ bgcolor: '#cfe8fc', height: '100vh' }}>
                <Divider />
                <form onSubmit={formik.handleSubmit}>
                    <FormGroup>
                        <Stack spacing={2}>
                            <FormControlLabel control={<Checkbox />} label="Delivering working, tested software every 3 weeks or less" />
                            <FormControlLabel control={<Checkbox />} label="Delivering what the business needs most" />
                            <FormControlLabel control={<Checkbox />} label="Process is continuously improving" />
                        </Stack>
                    </FormGroup>
                </form>
            </Box>
        </Container>
    );
};

export default WelcomeForm;
