import React from 'react';
import { FormikProvider, useFormik } from 'formik';
import Container from '@mui/material/Container';
import { useState } from 'react';
import Welcome from './Welcome';
import Step1 from './Step1';
import Step2 from './Step2';
import Step3 from './Step3';


const AssessmentForm = () => {
    const [step, setStep] = useState(1);

    const nextStep = () => {
        console.log('nextStep beginnen')
        setStep(step + 1);
        ///Log to the console to see what is happening
        console.log('nextStep', step);
    };
    const prevStep = () => {
        console.log('prevStep beginnen')
        setStep(step - 1);
        ///Log to the console to see what is happening
        console.log('prevStep', step);
    };
    const formik = useFormik({
        initialValues: {
            D1: false,
            B1: false,
            P1: false,
            D1_1: false,
            D1_1_1: false,
            D1_1_2: false,
        },
        onSubmit: (values) => {
            console.log('Form data', values);
            localStorage.setItem('formData', JSON.stringify(values));
        },
    });
    return (
        <FormikProvider value={formik}>
            <form onSubmit={formik.handleSubmit}>
                <Container sx={{ pt: 2 }}>
                    {step === 1 && <Welcome nextStep={nextStep} />}
                    {step === 2 && <Step1 prevStep={prevStep} nextStep={nextStep} />}
                    {step === 3 && <Step2 prevStep={prevStep} nextStep={nextStep} />}
                    {step === 4 && <Step3 prevStep={prevStep} handleSubmit={formik.handleSubmit} />}
                </Container>
            </form>
        </FormikProvider>
    );
};

export default AssessmentForm;
