import React from 'react';
import NavigationButtons from './NavButtons';
import AssessmentContent from './AssessmentContent';
import CheckboxField from './CheckboxField';
import { Typography, Card } from '@mui/material';
import Grid2 from '@mui/material/Unstable_Grid2/Grid2';


const Step2 = ({ prevStep, nextStep }) => {
    return (
        <React.Fragment>
            <AssessmentContent
                title="Delivering working, tested software every 3 weeks or less"
                prevStep={prevStep}
                nextStep={nextStep}>
                <Grid2 container spacing={2}>
                    <Grid2 xs={4}>
                    </Grid2>
                    <Grid2 xs={4}>
                        <Card>
                            <Typography variant='h6'>Iterations</Typography>
                            <CheckboxField name="D1_1" label="Timeboxed Iterations" />
                            <CheckboxField name="D1_1_1" label="Always end on time" />
                            <CheckboxField name="D1_1_2" label="Team not disrupted or controlled by outsiders" />
                        </Card>
                    </Grid2>
                    <Grid2 xs={4}>
                    </Grid2>
                </Grid2>
            </AssessmentContent>
            <NavigationButtons prevStep={prevStep} nextStep={nextStep} />
        </React.Fragment >
    );
};

export default Step2;
