import React, { useState, useEffect } from 'react';
import { Formik, Form, Field } from 'formik';

const initialValues = {
    deliveryFrequency: '',
    businessFocus: '',
    processImprovement: '',
};

const onSubmit = (values) => {
    localStorage.setItem('formValues', JSON.stringify(values));
};

const basics = () => {

    return (
        <div>
            <h1>The Basics</h1>
            <Formik initialValues={formValues} onSubmit={onSubmit}>
                <Form>
                    <div>
                        <label htmlFor="frequency">Delivering working, tested software every 3 weeks or less</label>
                        <Field name="frequency" type="checkbox" />
                    </div>
                    <div>
                        <label htmlFor="business">Delivering what the business needs most</label>
                        <Field name="business" type="checkbox" />
                    </div>
                    <div>
                        <label htmlFor="process">Process is continuously improving</label>
                        <Field name="process" type="checkbox" />
                    </div>
                    <button type="submit">Submit</button>
                </Form>
            </Formik>
        </div>
    );
};


export default basics;
