import React from 'react';
import { Field } from 'formik';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';

function CheckboxField({ name, label, indent, headlinestyle, ...rest }) {
    return (
        <Field type="checkbox" name={name}>
            {({ field }) => (
                <FormControlLabel
                    control={
                        <Checkbox
                            {...field}
                            id={name}
                            color="primary"
                            {...rest}
                            checked={field.value} // Set the checked state based on field.value
                            sx={{
                                paddingLeft: indent ? 5 : 2, // Add padding to the Checkbox component
                            }}
                        />
                    }
                    label={label}
                    sx={{
                        backgroundColor: headlinestyle ? '#61ddad' : 'transparent',
                        paddingLeft: indent ? 5 : 2, // Add padding to the label
                        paddingRight: 0,
                        textAlign: 'left',
                    }}
                />
            )}
        </Field>
    );
}

export default CheckboxField;
