import React, { useState } from 'react';
import { FormikProvider, useFormik } from 'formik';
import Container from '@mui/material/Container';
import Welcome from './Welcome';
import Step1 from './Step1';
import Step2 from './Step2';
import Step3 from './Step3';

const AssessmentForm = () => {
    const [step, setStep] = useState(1);

    const formik = useFormik({
        initialValues: {
            D1: false,
            B1: false,
            P1: false,
            D1_1: false,
            D1_1_1: false,
            D1_1_2: false,
        },
        onSubmit: (values) => {
            console.log('Form data', values);
            localStorage.setItem('formData', JSON.stringify(values));
        },
    });

    const handleNext = () => {
        console.log('Step Next', step);
        setStep(step + 1);
    };

    const handlePrev = () => {
        console.log('Step Next', step);
        setStep(step - 1);
    };

    return (
        <FormikProvider value={formik}>
            <form onSubmit={formik.handleSubmit}>
                <Container sx={{ pt: 2 }}>
                    {step === 1 && <Welcome onNext={handleNext} />}
                    {step === 2 && <Step1 onPrev={handlePrev} onNext={handleNext} />}
                    {step === 3 && <Step2 onPrev={handlePrev} onNext={handleNext} />}
                    {step === 4 && <Step3 onPrev={handlePrev} onSubmit={formik.handleSubmit} />}
                </Container>
            </form>
        </FormikProvider>
    );
};

export default AssessmentForm;
