import React from 'react';
import { Card, Stack } from '@mui/material';
import Typography from '@mui/material/Typography';
import NavigationButtons from './NavButtons';
import Grid2 from '@mui/material/Unstable_Grid2/Grid2';
import CheckboxField from './CheckboxField';
import AssessmentContent from './AssessmentContent';

const Step1 = ({ onPrev, onNext }) => {
    return (
        <AssessmentContent title="This is about the 3 most important things in Agile:" prevStep={prevStep} nextStep={nextStep}>
            <Typography variant="body2" gutterBottom>
                Delivering the products that your customers desire most to them as frequently as possible. Do you know what your customers need and do you keep working on your process?
            </Typography>
            <Grid2 container spacing={2}>
                <Grid2 xs={4}>
                </Grid2>
                <Grid2 xs={4}>
                    <Card>
                        <Stack>
                            <CheckboxField name="D1" label="Delivering working, tested software every 3 weeks or less" />
                            <CheckboxField name="B1" label="Delivering the highest business value features first" />
                            <CheckboxField name="P1" label="Continuously improving our processes" />
                        </Stack>
                    </Card>
                </Grid2>
                <Grid2 xs={4}>
                </Grid2>
            </Grid2>
            <NavigationButtons onPrevClick={onPrev} onNextClick={onNext} />
        </AssessmentContent>
    );
};

export default Step1;
