import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import Container from '@mui/material/Container';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Box from '@mui/material/Box';
import Divider from '@mui/material/Divider';
import Stack from '@mui/material/Stack';
import Item from '@mui/material/Stack';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
const AssessmentForm = () => {
    const [step, setStep] = useState(1);

    const nextStep = () => {
        setStep(step + 1);
    };
    const prevStep = () => {
        setStep(step - 1);
    };
    const formik = useFormik({
        initialValues: {
            frequency: '',
            business: '',
            process: '',
        },
    });
    return (
        <Container maxWidth="sm">
            <Box sx={{ bgcolor: 'green' }}>
                <Typography variant="body1" gutterBottom>
                    In this assessment, we will ask you a series of questions to determine how well your organization is doing with Agile.
                </Typography>
                <Divider />
            </Box>
            <Box sx={{ bgcolor: '#cfe8fc' }}>
                <Divider />
                <form>
                    <FormGroup>
                        <Stack spacing={2}>
                            <Item>
                                <FormControlLabel control={<Checkbox />} label="Delivering working, tested software every 3 weeks or less" />
                            </Item>
                            <Item>
                                <FormControlLabel control={<Checkbox />} label="Delivering what the business needs most" />
                            </Item>
                            <Item>
                                <FormControlLabel control={<Checkbox />} label="Process is continuously improving" />
                            </Item>
                        </Stack>
                    </FormGroup>
                    <Button variant="contained" color="primary" type="submit" onClick={nextStep}>
                        Submit
                    </Button>
                </form>
            </Box>
        </Container>
    );
};

export default AssessmentForm;
