import { createTheme } from '@mui/material/styles';

const DINFont = {
    fontFamily: 'DIN',
    fontStyle: 'normal',
    fontDisplay: 'swap',
    fontWeight: 400,
    src: `
    local('DIN'),
    url('/fonts/DIN-Regular.ttf') format('truetype')
  `,
};

const theme = createTheme({
    palette: {
        primary: {
            main: '#003971',
        },
        secondary: {
            main: '#f44336',
        },
    },
    typography: {
        fontFamily: 'DIN, Roboto, sans-serif', // add DIN font to font family
    },
    //appbar has max 1024px width and is centered
    //appbar has DIN bold font with a color #6BF3BF
    //appbar has a background color #003971
    appbar: {
        width: '100%',
        margin: '0 auto',
        fontFamily: 'DIN, Roboto, sans-serif',
        color: '#6BF3BF',
        backgroundColor: '#003971',
    },
    components: {
        MuiCssBaseline: {
            styleOverrides: {
                '@global': {
                    '@font-face': [DINFont], // add @font-face rule to load DIN font
                },
            },
        },
    },
});

export default theme;
