import React from 'react';
import { Card, Stack } from '@mui/material';
import { Container } from '@mui/material';
import Checkbox from '@mui/material/Checkbox';
import Typography from '@mui/material/Typography';
import FormControlLabel from '@mui/material/FormControlLabel';
import { Field } from 'formik';
import NavigationButtons from './NavButtons';
import Grid2 from '@mui/material/Unstable_Grid2/Grid2';

const Step1 = ({ prevStep, nextStep }) => {
    return (
        <Container>
            <Typography variant="h6" gutterBottom>
                This is about the 3 most important things in Agile:
            </Typography>
            <Typography variant="body2" gutterBottom>
                Delivering the products that your customers desire most to them as frequently as possible. Additionally we want to know, wether you are using continous process improvement to become even better at this.
            </Typography>
            <Grid2 container spacing={2}>
                <Grid2 xs={4}>
                </Grid2>
                <Grid2 xs={4}>
                    <Card>
                        <Stack>
                            <Field type="checkbox" name="frequency">
                                {({ field }) => (
                                    <FormControlLabel
                                        control={
                                            <Checkbox
                                                {...field}
                                                id="frequency"
                                                color="primary" // You can customize the color
                                            />
                                        }
                                        label="Delivering working, tested software every 4 weeks or less."
                                    />
                                )}
                            </Field>
                            <Field type="checkbox" name="business">
                                {({ field }) => (
                                    <FormControlLabel
                                        control={
                                            <Checkbox
                                                {...field}
                                                id="business"
                                                color="primary" // You can customize the color
                                            />
                                        }
                                        label="Delivering what the business needs most."
                                    />
                                )}
                            </Field>
                            <Field type="checkbox" name="process">
                                {({ field }) => (
                                    <FormControlLabel
                                        control={
                                            <Checkbox
                                                {...field}
                                                id="process"
                                                color="primary" // You can customize the color
                                            />
                                        }
                                        label="Process is continuously improving."
                                    />
                                )}
                            </Field>
                        </Stack>
                    </Card>

                </Grid2>
                <Grid2 xs={4}>
                </Grid2>
            </Grid2>
            <NavigationButtons onPrevClick={prevStep} onNextClick={nextStep} />
        </Container>
    );
};

export default Step1;
