import React from "react";
import { Container, Box, Grid } from "@mui/material";

///create a container for the assessment content
/// add a header as description
/// add a box for the checkbox questions
/// at the bottom there is a navigation buttons component
/// pass in the prevStep and nextStep functions

const AssessmentContent = ({ title, prevStep, nextStep, children }) => {
    return (
        <Container>
            <Box>
                <Grid>
                    {children}
                </Grid>
            </Box>
        </Container>
    );
};

export default AssessmentContent;
