import React from 'react';
import NavigationButtons from './NavButtons';
import AssessmentContent from './AssessmentContent';
import CheckboxField from './CheckboxField';
import { List, ListSubheader } from '@mui/material';

const Step2 = ({ prevStep, nextStep }) => {
    return (
        <React.Fragment>
            <AssessmentContent
                title="Assessment"
                prevStep={prevStep}
                nextStep={nextStep}>

//q: how to indent one of the checkboxes?
                ///create List with one sublist
                ///add a header as description
                <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
                    <h2>Product Owner</h2>
                    <CheckboxField name="productOwner" label="Clearly defined product owner" />
                    <ListSubheader component="div" id="nested-list-subheader">
                        <CheckboxField name="poEmpowered" label="PO is empowered to priotise" />
                        <CheckboxField name="poKnowlege" label="PO has knowledge to priotise" />

                    </ListSubheader>



                </List>
            </AssessmentContent>

            <NavigationButtons prevStep={prevStep} nextStep={nextStep} />
        </React.Fragment>
    );
};

export default Step2;
