import React from 'react';
import NavigationButtons from '../components/NavButtons';
import AssessmentContent from '../components/AssessmentContent';
import CheckboxField from '../components/CheckboxField';
import { Stack } from '@mui/material';
import Grid2 from '@mui/material/Unstable_Grid2/Grid2';
import AssessmentCard from '../components/AssessmentCard';
import Checklist from '../components/Checklist';

const Step2 = ({ onPrev, onNext }) => {

    return (
        <React.Fragment>
            <AssessmentContent
                title="Delivering working, tested software every 3 weeks or less">
                <Grid2 container spacing={2}>
                    <Grid2 xs={6}>
                        <AssessmentCard title="Iterations">
                            <Checklist>
                                <CheckboxField name="D1_1" label="Timeboxed Iterations" />
                            </Checklist>
                            <Checklist indent={true}>
                                <CheckboxField name="D1_1_1" label="Always end on time" indent={true} />
                                <CheckboxField name="D1_1_2" label="Team not disrupted or controlled by outsiders" indent={true} />
                            </Checklist>
                        </AssessmentCard>
                    </Grid2>
                    <Grid2 xs={6}>
                        <AssessmentCard title="Testing">
                            <Checklist>
                                <CheckboxField name="D1_1_3" label="Test Driven Development" headlinestyle={true} />
                            </Checklist>
                            <Checklist xs={12} pl={4}>

                                <CheckboxField name="D1_1_4" label="Automated Testing" indent={true} />
                                <CheckboxField name="D1_1_5" label="Continuous Integration" indent={true} />
                            </Checklist>
                        </AssessmentCard>
                    </Grid2>
                </Grid2>
            </AssessmentContent>
            <NavigationButtons onPrevClick={onPrev} onNextClick={onNext} />
        </React.Fragment >
    );
};

export default Step2;
