// Note: This is the fourth step of the assessment process
import Grid2 from '@mui/material/Unstable_Grid2/Grid2';
import AssessmentContent from '../components/AssessmentContent';
import React from 'react';
import AssessmentCard from '../components/AssessmentCard';
import Checklist from '../components/Checklist';
import CheckboxField from '../components/CheckboxField';


const Step4 = (onPrev, onNext) => {
    return (
        <React.Fragment>
            <AssessmentContent
                title="Continuously improving the process">
                <Grid2 container spacing={2}>
                    <Grid2 xs={6}>
                        <AssessmentCard title="Retrospectives">
                            <Checklist>
                            </Checklist>
                        </AssessmentCard>

                    </Grid2>
                    <Grid2 xs={6}>
                        <AssessmentCard title="Definition of Done">
                            <Checklist>
                                <CheckboxField name="P1_1" label="Definition of Done" headlinestyle={true} />
                                <CheckboxField name="P1_2" label="Definition of Ready" headlinestyle={false} />
                            </Checklist>
                        </AssessmentCard>
                    </Grid2>
                </Grid2>
            </AssessmentContent>
        </React.Fragment >
    );
}
export default Step4;
