import React from 'react';
import NavigationButtons from './NavButtons';
import AssessmentContent from './AssessmentContent';
import CheckboxField from './CheckboxField';
import { Typography, Card, Stack } from '@mui/material';
import Grid2 from '@mui/material/Unstable_Grid2/Grid2';

const Step3 = ({ onPrev, onNext }) => {

    return (
        <React.Fragment>
            <AssessmentContent
                title="Delivering working, tested software every 3 weeks or less">
                <Grid2 container spacing={2}>
                    <Grid2 xs={4}>
                    </Grid2>
                    <Grid2 xs={4}>
                        <Card>
                            <Typography variant='h6'>Iterations</Typography>
                            <Stack>
                                <CheckboxField name="D1_1" label="Timeboxed Iterations" headlinestyle={true} />
                                <CheckboxField name="D1_1_1" label="Always end on time" indent={true} />
                                <CheckboxField name="D1_1_2" label="Team not disrupted or controlled by outsiders" indent={true} />
                            </Stack>
                        </Card>
                    </Grid2>
                    <Grid2 xs={4}>
                    </Grid2>
                </Grid2>
            </AssessmentContent>
            <NavigationButtons onPrevClick={onPrev} onNextClick={onNext} />
        </React.Fragment >
    );
};

export default Step3;
